
public class operators_Assignmnt2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 20;
		int b = 3;
		
		System.out.println(" Arithmetic operators \n ");
		int add = a + b;
		System.out.println(" Addition of two numbers "+ add + "\n");
		
		int sub = a - b;
		System.out.println(" Subtraction of two numbers "+ sub + "\n");
		
		int div = a / b;
		System.out.println(" Division of two numbers "+ div + "\n");
		
		int mod = a % b;
		System.out.println(" Modulo of two numbers "+ mod + "\n");
		
	
		int inc = ++a;
		System.out.println(" Increment Operator "+ inc + "\n");
		
		
		int dec = --a;
		System.out.println(" Decrement Operator "+ dec + "\n");
		
		System.out.println("-----------------------------------");
		
		System.out.println(" Assignment operators \n ");
		
		a += b;
		System.out.println(" Assign & Addition of two numbers "+ a + "\n");
		
		a -= b;
		System.out.println(" Assign & Subtraction of two numbers "+ a + "\n");
		
		a /= b;
		System.out.println(" Assign & Division of two numbers "+ a + "\n");
		
		a %= b;
		System.out.println(" Assign & Modulo of two numbers "+ a + "\n");
		
	
	}

}
